// ==UserScript==
// @name           Enhanced Etherpad
// @description    Removes useless footer and header.
// @version        0.1
// @grant          none
// @include        http://piratenpad.de/*
// @include        http://*.piratenpad.de/*
// @include        https://piratenpad.de/*
// @include        https://*.piratenpad.de/*
// @include        http://piratepad.net/*
// @include        http://*.piratepad.net/*
// @include        http://titanpad.com/*
// @include        http://*.titanpad.com/*
// @include        https://etherpad.mozilla.org/*
// @include        https://*.etherpad.mozilla.org/*
// @include        http://*ponypad*.*/*
// ==/UserScript==

(function() {
	if(!document.querySelector || !document.querySelector('#padpage #padmain'))
		return;
	var isChrome = navigator.userAgent.toLowerCase().indexOf('chrome')>=0;
	var isOpera = navigator.userAgent.toLowerCase().indexOf('opera')>=0;
	var isSafari = navigator.userAgent.toLowerCase().indexOf('safari')>=0;
	if(isChrome) {
		var script = document.createElement('script');
		script.setAttribute('type','text/javascript');
		script.textContent = '('+main.toString()+')();';
		document.head.appendChild(script);
	}
	else if(isSafari) {
		window.addEventListener('load', function() {
			var script = document.createElement('script');
			script.textContent = '('+main.toString()+')();';
			document.head.appendChild(script);
		}, false);
	}
	else
		main();
	function main() {
		// Header
		$("#topbar").hide();
		$("#docbar").css('margin-top', '0px');
		$("#padmain").css('top', '25px');
		$("#docbarslider-outer").removeClass('highlight');
		var fullscreen_normal = 'data:image/png;base64,';
		$("#docbarslider-outer").after('<td id="docbarfullsreen-outer" class="docbarbutton highlight">\
			<a href="javascript:void(0);" id="docbarfullsreen" title="Toggle maximization">\
			<img src="'+fullscreen_normal+'">Full screen</a>\
			</td>');
		$('#docbarfullsreen-outer').click(function() {
			if($('body').hasClass('maximized'))
				$("#docbarfullsreen-outer img")[0].src = 'data:image/png;base64,';
			else
				$("#docbarfullsreen-outer img")[0].src = 'data:image/png;base64,';
			$('body').toggleClass('maximized');
		});

		$('#docbarpadtitle').before('<td id="docbarback-outer" class="docbarbutton">\
			<a href="/ep/padlist/" id="docbarback" title="Return to pad list"></a>\
			</td>');
		$('#docbarback-outer a').css('font-size', '18px');

		$('#docbarpadtitle').css('left', '32px');
		$('#docbarrenamelink').css('left', (parseInt($('#docbarrenamelink')[0].style.left)+23) + 'px');

		// var left = 'data:image/png;base64,';
		// var right_orange = 'data:image/png;base64,';
		// $('#padtop table tbody tr td img')[0].src = left;
		// $('#padtop table tbody tr td img')[$('#padtop table tbody tr td img').length - 1].src = right_orange;

		// Footer
		$("#footerbar").hide();
		$("#padeditor").css('bottom', '0px');
		$("#vdraggie").css('bottom', '0px');
		$("#padsidebar").css('bottom', '0px');
	}
})();
